import React, { ReactElement } from "react";
import styled from "styled-components/native";

interface Props {
  title: string;
  description: string;
  index: number;
}

export default function Tour({
  title,
  description,
  index,
}: Props): ReactElement {
  return (
    <Styled.container>
      <Styled.image source={require(`../../assets/fm-tour/0.svg`)} />
      <Styled.heading>{title}</Styled.heading>
    </Styled.container>
  );
}

const Styled = {
  container: styled.View`
    flex: 1;
    background: #f7f7fc;
  `,
  heading: styled.Text`
    font-size: 20px;
  `,
  image: styled.Image`
    width: 288px;
    height: 321px;
  `,
  main: styled.View`
    border-radius: 24px;
    background: #fff;
  `,
};
