import React, { ReactElement } from "react";
import styled from "styled-components/native";
import * as Contacts from "expo-contacts";
import { SafeAreaView } from "react-native-safe-area-context";
import { ScrollView } from "react-native";

interface Props {
  navigation: any;
}

const PAGES = [
  {
    heading: "Inner Circle",
    text: "Inner Circle is the circle of the closest people to you. You may not talk daily, but they are close to Heart.",
  },
  {
    heading: "Close Friends",
    text: "Close Friends are the ones just next after the Inner Circle. You love their company and cherish it.",
  },
  {
    heading: "Normal Friends",
    text: "Normal Friends - Those with whom you experience Positivity and want them to be better friends.",
  },
];

export default function ContactsComponent({ navigation }: Props): ReactElement {
  const [progress, setProgress] = React.useState(0);
  const [error, setError] = React.useState(false);
  const [data, setData] = React.useState<any>({});
  const [selected, setSelected] = React.useState({
    "inner-circle": [],
    "close-friends": [],
    "normal-friends": [],
  });

  React.useEffect(() => {
    (async () => {
      const { status } = await Contacts.requestPermissionsAsync();
      if (status === "granted") {
        const { data } = await Contacts.getContactsAsync({
          fields: [],
        });

        if (data.length < 0) setError(true);

        const cleanedData = data.map((contact) => {
          let name = contact.firstName;
          if (contact.lastName) name += ` ${contact.lastName}`;
          if (name?.includes("10000 characters")) name = contact.firstName;
          return {
            name,
            //@ts-ignore
            lookupKey: contact.lookupKey,
          };
        });

        const segregatedData: any = {};
        cleanedData.forEach((contact) => {
          if (!contact.name) return;
          if (!segregatedData[contact.name[0].toLowerCase()])
            segregatedData[contact.name[0].toLowerCase()] = [];
          segregatedData[contact.name[0].toLowerCase()].push(contact);
        });

        setData(segregatedData);
      }
    })();
  }, []);

  return (
    <Styled.container>
      <Styled.topBar>
        <Styled.back
          onPress={() => {
            if (progress === 0) return navigation.navigate("Home");
            setProgress((p) => p - 1);
          }}
        >
          {"< Back"}
        </Styled.back>
        <Styled.next onPress={() => setProgress((p) => p + 1)}>
          Next
        </Styled.next>
      </Styled.topBar>

      <Styled.main>
        <Styled.search>
          <Styled.searchHeading>
            Add people {"\n"}to{" "}
            <Styled.searchHeadingTitle>
              {PAGES[progress].heading}
            </Styled.searchHeadingTitle>
          </Styled.searchHeading>
          <Styled.searchDescription>
            {PAGES[progress].text}
          </Styled.searchDescription>
        </Styled.search>
        <Styled.list>
          {Object.keys(data).map((letter: string, i) => (
            <Styled.card key={i}>
              <Styled.cardLetter>{letter.toUpperCase()}</Styled.cardLetter>
              <ScrollView>
                <Styled.cardContacts>
                  {data[letter].map((contact: any) => (
                    <Styled.cardContact>
                      <Styled.cardContactLetterBox>
                        <Styled.cardContactLetter>
                          {letter.toUpperCase()}
                        </Styled.cardContactLetter>
                      </Styled.cardContactLetterBox>
                      <Styled.cardContactMid>
                        <Styled.cardContactMidName>
                          {contact.name}
                        </Styled.cardContactMidName>
                      </Styled.cardContactMid>
                    </Styled.cardContact>
                  ))}
                </Styled.cardContacts>
              </ScrollView>
            </Styled.card>
          ))}
        </Styled.list>
      </Styled.main>
    </Styled.container>
  );
}

const Styled = {
  container: styled(SafeAreaView)`
    background-color: #fff;
  `,
  card: styled.View`
    min-height: 200px;
    margin: 10px 0;
    background: #fff;
    border-radius: 12px;
  `,
  cardLetter: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 19px;
    color: #2e303a;
    padding: 10px;
  `,
  cardContacts: styled.ScrollView``,
  cardContact: styled.View`
    flex-direction: row;
    padding: 10px;
  `,
  cardContactLetterBox: styled.View`
    background-color: #4861e6;
    width: 48px;
    height: 48px;
    border-radius: 12px;
    align-items: center;
    justify-content: center;
  `,
  cardContactLetter: styled.Text`
    font-family: "Lexend_400Regular";
    font-style: normal;
    font-weight: 400;
    font-size: 22.6154px;
    line-height: 28px;
    color: #ffffff;
  `,
  cardContactMid: styled.View``,
  cardContactMidName: styled.Text``,
  topBar: styled.View`
    height: 60px;
    justify-content: center;
    justify-content: space-between;
    padding: 20px 30px;
    flex-direction: row;
  `,
  main: styled.View`
    flex: 1;
  `,
  search: styled.View`
    background: #465fde;
    border-radius: 24px;
    height: 250px;
    margin: 20px;
    padding: 20px;
  `,
  searchHeading: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 24px;
    line-height: 28px;
    color: #d2daff;
  `,
  searchHeadingTitle: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 24px;
    line-height: 28px;
    color: #ffffff;
  `,
  searchDescription: styled.Text`
    font-family: "Lexend_400Regular";
    font-style: normal;
    font-weight: 400;
    font-size: 14px;
    line-height: 18px;
    color: #d2daff;
    margin-top: 10px;
  `,
  list: styled.View`
    margin: 20px;
  `,
  cards: styled.View`
    flex: 1;
    height: 2000px;
    background-color: red;
  `,
  next: styled.Text``,
  back: styled.Text``,
};
