import React, { ReactElement } from "react";
import styled from "styled-components/native";
import { SafeAreaView } from "react-native-safe-area-context";
import { TouchableWithoutFeedback } from "react-native";

const zero = require(`../../assets/FM/tour/0.png`);
const one = require(`../../assets/FM/tour/1.png`);
const two = require(`../../assets/FM/tour/2.png`);
const three = require(`../../assets/FM/tour/3.png`);
const four = require(`../../assets/FM/tour/4.png`);
const five = require(`../../assets/FM/tour/5.png`);

const IMAGES = [zero, one, two, three, four, five];
const TOTAL = 6;
const TOUR = [
  {
    title: "Connection Stats",
    description: "Stay updated with your friends circles",
  },
  {
    title: "Add Connections",
    description: "Create New or Import from contact list",
  },
  {
    title: "Your Connections",
    description: "Get notified if you are losing touch with your friends",
  },
  {
    title: "Easy To Plan",
    description: "Plan activities with your friends based on suggestions",
  },
  {
    title: "Forgot Birthday?",
    description:
      "Easy to track upcoming birthdays so you can plan ahead for suprises.",
  },
  {
    title: "Track Events",
    description: "Get alerts for upcoming events with your friend circles.",
  },
];

export default function Tour({ navigation }: any): ReactElement {
  const [index, setIndex] = React.useState(0);

  React.useEffect(() => {
    if (index === -1) {
      navigation.navigate("Home");
    }
  }, [index]);

  const renderDots = (number: number) =>
    [...Array(number)].map((_, i) => <Styled.dot key={i}></Styled.dot>);

  return (
    <Styled.container>
      <Styled.top>
        <Styled.back
          onPress={() => {
            if (index === 0) return navigation.navigate("Home");
            setIndex((p) => p - 1);
          }}
        >
          Back
        </Styled.back>
        <Styled.skip onPress={() => navigation.navigate("Home")}>
          Skip
        </Styled.skip>
      </Styled.top>
      <Styled.main>
        <Styled.image source={IMAGES[index]} />
        <Styled.heading>{TOUR[index].title}</Styled.heading>
        <Styled.description>{TOUR[index].description}</Styled.description>
      </Styled.main>
      <Styled.bottom>
        <Styled.dots>
          {renderDots(index)}
          <Styled.line></Styled.line>
          {renderDots(TOTAL - index - 1)}
        </Styled.dots>
        <TouchableWithoutFeedback
          onPress={() => {
            if (index === TOTAL - 1) {
              navigation.navigate("Home");
              setIndex(0);
              return;
            }
            setIndex((p) => p + 1);
          }}
        >
          <Styled.next>
            <Styled.arrow
              source={require(`../../assets/FM/icons/tour_right.png`)}
            />
          </Styled.next>
        </TouchableWithoutFeedback>
      </Styled.bottom>
    </Styled.container>
  );
}

const Styled = {
  container: styled(SafeAreaView)`
    flex: 1;
    justify-content: space-between;
    background: #f7f7fc;
  `,
  line: styled.View`
    width: 30px;
    height: 8px;
    background: #4861e6;
    border-radius: 56px;
    margin-right: 3px;
  `,
  dots: styled.View`
    flex-direction: row;
    align-items: center;
  `,
  dot: styled.View`
    background: #8b8ea7;
    opacity: 0.3;
    border-radius: 56px;
    width: 8px;
    height: 8px;
    margin-right: 3px;
  `,
  back: styled.Text`
    font-weight: 600;
    font-size: 14px;
    color: #2e303a;
  `,
  skip: styled.Text`
    font-weight: 600;
    font-size: 14px;
    color: #767a8d;
  `,
  heading: styled.Text`
    font-weight: 700;
    font-size: 22px;
    color: #4e66e5;
    margin-top: auto;
    margin-left: 3px;
  `,
  description: styled.Text`
    font-size: 14px;
    color: #767a8d;
    margin-top: 7px;
    margin-bottom: 25px;
    margin-left: 3px;
  `,
  image: styled.Image`
    width: 288px;
    aspect-ratio: 288/321;
    width: 100%;
    /* height: 321px; */
    align-self: center;
  `,
  next: styled.View`
    background: #4861e6;
    border-radius: 12px;
    height: 45px;
    width: 45px;
    justify-content: center;
    position: absolute;
    top: -15px;
    right: 20px;
  `,
  arrow: styled.Image`
    width: 21.885px;
    height: 12px;
    align-self: center;
  `,
  main: styled.View`
    border-radius: 24px;
    background: #fff;
    border-radius: 24px;
    padding: 20px;
    margin: 20px 20px 80px 20px;
    flex: 1;
  `,
  top: styled.View`
    height: 80px;
    flex-direction: row;
    justify-content: space-between;
    padding: 20px 30px;
  `,
  bottom: styled.View`
    background: #fff;
    height: 80px;
    padding: 10px 20px;
  `,
  backIcon: styled.Image``,
};
