import React, { ReactElement } from "react";
import styled from "styled-components/native";
import TagSelect from "../../components/TagSelect";

interface Props {}

export default function Connection({}: Props): ReactElement {
  const [notes, setNotes] = React.useState<string[]>([]);
  const [reminders, setReminders] = React.useState<
    { text: string; date: number }[]
  >([]);

  return (
    <Styled.container>
      <TagSelect
        heading={"Select the Tags which best describes your bond with them"}
        max={3}
        options={[
          "Friends",
          "Family",
          "School",
          "College",
          "Network",
          "Services",
        ]}
      />
      <TagSelect
        heading={"Select the Tags which best describes your bond with them"}
        max={3}
        options={[
          "Friends",
          "Family",
          "School",
          "College",
          "Network",
          "Services",
        ]}
      />
      <TagSelect
        heading={"Select the Tags which best describes your bond with them"}
        max={3}
        options={[
          "Friends",
          "Family",
          "School",
          "College",
          "Network",
          "Services",
        ]}
      />
    </Styled.container>
  );
}

const Styled = {
  container: styled.View`
    flex: 1;
  `,
  heading: styled.Text``,
};
