import React, { ReactElement } from "react";
import styled from "styled-components/native";
import { widthInPercent } from "../../utils/utilities";

interface Props {
  title: string;
  description: string;
  index: number;
}

export default function Tour({
  title,
  description,
  index,
}: Props): ReactElement {
  return (
    <Styled.container>
      <Styled.top></Styled.top>
      <Styled.main>
        <Styled.image source={require(`../../assets/fm-tour/0.png`)} />
        <Styled.heading>{title}</Styled.heading>
        <Styled.description>{description}</Styled.description>
      </Styled.main>
      <Styled.bottom></Styled.bottom>
    </Styled.container>
  );
}

const Styled = {
  container: styled.View`
    flex: 1;
    justify-content: space-between;
    background: #f7f7fc;
  `,
  heading: styled.Text`
    font-weight: 700;
    font-size: 22px;
    color: #4e66e5;
    margin-top: 30px;
  `,
  description: styled.Text`
    font-size: 14px;
    color: #767a8d;
    margin-top: 7px;
  `,
  image: styled.Image`
    width: 288px;
    height: 321px;
    align-self: center;
  `,
  main: styled.View`
    border-radius: 24px;
    background: #fff;
    border-radius: 24px;
    padding: 20px;
    margin: 50px 20px 80px 20px;
    flex: 1;
  `,
  top: styled.View`
    height: 80px;
  `,
  bottom: styled.View`
    background: #fff;
    height: 80px;
  `,
};
