import React, { ReactElement } from "react";
import styled from "styled-components/native";
import { SafeAreaView } from "react-native-safe-area-context";
import RBSheet from "react-native-raw-bottom-sheet";
import { TouchableWithoutFeedback } from "react-native";
import Connections from "./Connections";
import Timeline from "./Timeline";

interface Props {
  navigation?: any;
}

export default function Home({ navigation }: Props): ReactElement {
  const refRBSheet = React.useRef();
  const [connectionsSelected, setConnectionsSelected] = React.useState(false);

  return (
    <Styled.container>
      <RBSheet
        //@ts-ignore
        ref={refRBSheet}
        closeOnDragDown={true}
        customStyles={{
          container: {
            borderRadius: 32,
          },
          wrapper: {
            backgroundColor: "transparent",
          },
          draggableIcon: {
            backgroundColor: "#BDC6EC",
          },
        }}
      ></RBSheet>
      <TouchableWithoutFeedback onPress={() => navigation.navigate("Tour")}>
        <Styled.topRight onPress={() => navigation.navigate("Tour")}>
          <Styled.bulb
            source={require("../../assets/FM/icons/bulb.png")}
          ></Styled.bulb>
        </Styled.topRight>
      </TouchableWithoutFeedback>
      <Styled.heading>Manage {"\n"}Life Connections</Styled.heading>
      <Styled.subheading>
        Reminders to stay in touch with the people you love
      </Styled.subheading>
      <Styled.nav>
        <TouchableWithoutFeedback onPress={() => setConnectionsSelected(true)}>
          <Styled.navItem selected={connectionsSelected}>
            <Styled.navItemText>Connections</Styled.navItemText>
          </Styled.navItem>
        </TouchableWithoutFeedback>
        <TouchableWithoutFeedback onPress={() => setConnectionsSelected(false)}>
          <Styled.navItem selected={!connectionsSelected}>
            <Styled.navItemText>Timeline</Styled.navItemText>
          </Styled.navItem>
        </TouchableWithoutFeedback>
      </Styled.nav>
      {connectionsSelected ? (
        <Connections navigation={navigation} />
      ) : (
        <Timeline />
      )}
    </Styled.container>
  );
}

const Styled = {
  container: styled(SafeAreaView)`
    flex: 1;
    padding: 30px;
  `,
  topRight: styled.View`
    width: 44px;
    height: 44px;
    background: #ffffff;
    box-shadow: 0px 3.52px 3.52px rgba(102, 116, 166, 0.06);
    border-radius: 440px;
    justify-content: center;
    align-items: center;
    position: absolute;
    right: 25px;
    top: 35px;
  `,
  bulb: styled.Image``,
  heading: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 24px;
    line-height: 30px;
    color: #2e303a;
  `,
  subheading: styled.Text`
    font-family: "Lexend_400Regular";
    font-style: normal;
    font-weight: 400;
    font-size: 12px;
    line-height: 15px;
    color: #767a8d;
    margin-top: 8px;
  `,
  nav: styled.View`
    min-width: 320px;
    width: 100%;
    height: 58px;
    background: #edf0fc;
    border-radius: 12px;
    margin: 25px auto;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  `,
  navItem: styled.View<{ selected: boolean }>`
    flex: 1;
    margin: 10px;
    ${({ selected }: any) =>
      selected &&
      `
    background: #ffffff;
    box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.1);
    border-radius: 12px;
    width: 147px;
    height: 42px;
    justify-content: center;
  `}
  `,
  navItemText: styled.Text`
    font-family: "Lexend_600SemiBold";
    font-style: normal;
    font-weight: 600;
    font-size: 15px;
    line-height: 18px;
    text-align: center;
    color: #2e303a;
  `,
};
