import React, { ReactElement } from "react";
import styled from "styled-components/native";
import { ScrollView, TouchableWithoutFeedback } from "react-native";
import RBSheet from "react-native-raw-bottom-sheet";

interface Props {
  navigation: any;
}

export default function Connections({ navigation }: Props): ReactElement {
  const refRBSheet = React.useRef();

  return (
    <ScrollView>
      <Styled.container>
        <RBSheet
          //@ts-ignore
          ref={refRBSheet}
          closeOnDragDown={true}
          height={200}
          customStyles={{
            container: {
              borderRadius: 32,
            },
            wrapper: {
              backgroundColor: "transparent",
            },
            draggableIcon: {
              backgroundColor: "#BDC6EC",
            },
          }}
        ></RBSheet>
        <Styled.infoCard>
          <Styled.infoCardTop></Styled.infoCardTop>
        </Styled.infoCard>
        <Styled.divider>
          <TouchableWithoutFeedback
            onPress={() => {
              // @ts-ignore
              refRBSheet.current.open();
            }}
          >
            <Styled.add>
              <Styled.plus source={require("../../assets/FM/icons/plus.png")} />
            </Styled.add>
          </TouchableWithoutFeedback>
        </Styled.divider>
        <Styled.heading>Your Connections</Styled.heading>
        <Styled.search>
          <Styled.searchBar placeholder={"Search"} />
          <Styled.filters></Styled.filters>
        </Styled.search>
      </Styled.container>
    </ScrollView>
  );
}

const Styled = {
  heading: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 24px;
    line-height: 30px;
    color: #2e303a;
    margin-top: 20px;
  `,
  container: styled.View`
    flex: 1;
  `,
  search: styled.View`
    flex-direction: row;
  `,
  searchBar: styled.TextInput`
    flex: 4;
    font-size: 16px;
    padding: 5px 10px;
    margin-top: 10px;
    background: #ffffff;
    border: 1px solid #f0f0f8;
    border-radius: 10px;
  `,
  filters: styled.View`
    flex: 1;
  `,
  infoCard: styled.View`
    width: 320px;
    height: 303px;
    background: #687eee;
    border-radius: 24px;
    box-shadow: 0px 8px 40px rgba(66, 83, 146, 0.12);
  `,
  infoCardText: styled.View``,
  infoCardTextBold: styled.View``,
  infoCardTop: styled.View``,
  infoCardMid: styled.View``,
  infoCardBottom: styled.View``,
  infoCardButtons: styled.View``,
  infoCardAddButton: styled.View``,
  infoCardContactsButton: styled.View``,
  divider: styled.View`
    background-color: #eceef4;
    height: 2px;
    margin-top: 50px;
  `,
  add: styled.View`
    position: absolute;
    right: 0;
    top: -32px;
    width: 64px;
    height: 64px;
    background: #465fde;
    border-radius: 50px;
    justify-content: center;
    align-items: center;
  `,
  plus: styled.Image``,
};
