import React, { ReactElement } from "react";
import { View } from "react-native";
import styled from "styled-components/native";

interface Props {
  heading: string;
  max: number;
  options: string[];
  twoColumn?: boolean;
}

const WORDS = [null, null, "Two", "Three", "Four", "Five"];

export default function TagSelect({
  heading,
  max,
  options,
  twoColumn = false,
}: Props): ReactElement {
  return (
    <Styled.container>
      <Styled.max>{max === 1 ? "Select One" : `Max ${WORDS[max]}`}</Styled.max>
      <Styled.heading>{heading}</Styled.heading>
      <Styled.options>
        {options.map((option: string) => (
          <Styled.option>{option}</Styled.option>
        ))}
      </Styled.options>
    </Styled.container>
  );
}

const Styled = {
  container: styled.View`
    /* background: radial-gradient(
      103.28% 101.91% at 102.03% 101.91%,
      #ffffff 0%,
      #fbfcfd 100%
    ); */
    box-shadow: 0px 8px 40px rgba(66, 83, 146, 0.12);
    border-radius: 12px;
    margin: 20px;
    padding: 20px;
  `,
  heading: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 17px;
    line-height: 24px;
    color: #2e303a;
    margin: 10px 0;
  `,
  max: styled.Text`
    font-family: "Lexend_700Bold";
    font-style: normal;
    font-weight: 700;
    font-size: 12px;
    line-height: 131.1%;
    color: #465fde;
  `,
  options: styled.View`
    flex-direction: row;
    flex-wrap: wrap;
  `,
  option: styled.View`
    padding: 10px 15px;
    background: #f5f6fa;
    border-radius: 12px;
    align-items: center;
    justify-content: center;
    margin: 7px;
    font-family: "Lexend";
    font-style: normal;
    font-weight: 400;
    font-size: 14px;
  `,
};
